INSERT INTO `library_db`.`quotes` (`quotes_id`,`quotes_book_id`,`quotes_text`,`quotes_add_datetime`,`quotes_source`) VALUES (1,NULL,'Слишком много красного было в этой семейке.\r\n А рядом, прислонившись к стене, стоял Красный Дракон. Единственный по-настоящему красный в этой компании. Потому что красный цвет коварен. Его можно носить и мазать на лицо до одурения, делаясь только серее. Красный — цвет убийц, колдунов и клоунов..','2014-04-22 03:02:57',NULL);
