INSERT INTO `library_db`.`quotes` (`quotes_id`,`quotes_book_id`,`quotes_text`,`quotes_add_datetime`,`quotes_source`) VALUES (8,1,'И пусть мне не рассказывают о крысах и их повадках. Держал я настоящую крысу. Не из белых, а самую что ни на есть доподлинно серую. С ней можно было спать ложиться. Кормишь с рук и всё. Никаких подвохов. Но Человек - совсем другое дело. Корми не корми, а близко не подходи.','2014-04-01 08:04:15',NULL);